sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	gs.info("**** Get Remediation Reassignment Request Started ***** ");

	var writer = response.getStreamWriter(),
	hdrs = {},
		record_limit = 100,
		gr_incidents = new GlideRecord('x_77590_dlp_eur_dlp_incidents');

		hdrs['Content-Type'] = 'application/json';
		response.setStatus(200);
		response.setHeaders(hdrs);

		gr_incidents.setLimit(record_limit);
		gr_incidents.addQuery('isremediationactionrequested', false);
		gr_incidents.addQuery('isreassigned', true);
		gr_incidents.query();

		var uuid = gs.generateGUID();

		writer.writeString("{\"uuid\" : \"" +uuid + "\" , \"reassignments\" : ");

			var reassignments = [];

			gs.info("#### Incidnets Count ##### " + gr_incidents.getRowCount());

			while (gr_incidents.hasNext()) {

				var reassignment = {};

					var incident_rec = 	gr_incidents.next();

					gs.info("#### Got Incident ######");
					reassignment.incidentId = gr_incidents.incidentid + '';
					reassignment.remediatorName = gr_incidents.reassigned_to.name + '';
					reassignment.remediatorEmail = gr_incidents.reassigned_to.email + '';
					reassignments.push(reassignment);
				}
				writer.writeString(global.JSON.stringify(reassignments));

				//  close the response object
				writer.writeString("}");
				gs.info("**** Get Remediation Reassignment Request Completed ***** ");
			})(request, response);
